package com.cpg.corejava.basics;

public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean i = true;
		boolean j = false;
		boolean m = i && j;
		System.out.println(m);
		boolean o = i || j;
		System.out.println(o);
		boolean n = !i;
		System.out.println(n);

	}

}
