package com.cpg.corejava.cfs;

public class IfElseExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 
		int i = 10;
		int j = 20;
		
		if(i<10)
		{
			System.out.println("i is single digit");
		}
		else
		{
			System.out.println("i is double digit");
		}
		
		if(j<10)
		{
			System.out.println("j is single digit");
		}
		else
		{
			System.out.println("j is double digit");
		}
		
		System.out.println("Executation completed");

	}

}
