package com.cpg.corejava.loop;

public class Loopinsideifelse {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int i =13;
		if(i%2==0)
		{
			for(int j =1;j<=5;j++)
			{
				System.out.println("The no. is even and displayed 5 times ");
			}
		}
		else
		{
			for(int m =1;m<=5;m++)
			{
				System.out.println("The no. is odd and displayed 5 times ");
			}
		}

	}

}
